import React from "react"
import "./form-input.styles.scss";

const FormInput = ({ onChangeProp, label, ...otherProps}) => (
     <div className="group">
          <input 
          className ="form-input"
          onChange={onChangeProp}
          {...otherProps}
          />
          {
               label
               ?
               (<label
                    className={`${
                         otherProps.value.length
                    ?
                    "shrink"
                    :
                    ""}form-input-label`}>
                    {label}
               </label>)
               :
                null
          }
     </div>
)
export default FormInput;
